import React, { useEffect, useState } from "react";

function App() {
  const [carros, setCarros] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch("http://localhost:3001/api/carros")
      .then((res) => res.json())
      .then((data) => {
        setCarros(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Erro ao buscar dados da API:", err);
        setLoading(false);
      });
  }, []);

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      <h1>üì¶ Consulta de Baterias</h1>
      {loading ? (
        <p>Carregando dados...</p>
      ) : (
        <ul>
          {carros.map((carro) => (
            <li key={carro.idcarro} style={{ marginBottom: "1rem" }}>
              <strong>{carro.nome}</strong> ({carro.ano}) - {carro.marca}<br />
              üîã Amperagem: {carro.amper} | Polo: {carro.lado} | CCA: {carro.cca}
              {carro.obs && <><br />üìù Obs: {carro.obs}</>}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default App;
