import React, { useEffect, useState } from "react";

function App() {
  const [carros, setCarros] = useState([]);
  const [loading, setLoading] = useState(false);
  const [search, setSearch] = useState("");

  const fetchCarros = (query = "") => {
    setLoading(true);
    fetch(`http://localhost:3001/api/carros?search=${encodeURIComponent(query)}`)
      .then((res) => res.json())
      .then((data) => {
        setCarros(data);
        setLoading(false);
      })
      .catch((err) => {
        console.error("Erro ao buscar dados da API:", err);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchCarros(search);
  }, [search]);

  return (
    <div style={{ padding: "2rem", fontFamily: "Arial" }}>
      <h1>üì¶ Consulta de Baterias</h1>

      <input
        type="text"
        placeholder="Buscar por nome, marca ou amperagem..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{
          padding: "0.5rem",
          marginBottom: "1.5rem",
          width: "100%",
          maxWidth: "400px",
          fontSize: "1rem",
          borderRadius: "4px",
          border: "1px solid #ccc",
        }}
      />

      {loading ? (
        <p>Carregando dados...</p>
      ) : (
        <ul>
          {carros.length === 0 ? (
            <p>Nenhum resultado encontrado.</p>
          ) : (
            carros.map((carro) => (
              <li key={carro.idcarro} style={{ marginBottom: "1rem" }}>
                <strong>{carro.nome}</strong> ({carro.ano}) - {carro.marca}
                <br />
                üîã Amperagem: {carro.amper} | Polo: {carro.lado} | CCA: {carro.cca}
                {carro.obs && (
                  <>
                    <br />
                    üìù Obs: {carro.obs}
                  </>
                )}
              </li>
            ))
          )}
        </ul>
      )}
    </div>
  );
}

export default App;
